using NLog ;
using NLog.Targets ;
using Xunit.Abstractions ;

namespace Tests
{
    /// <summary></summary>
    /// <seealso cref="NLog.Targets.TargetWithLayout" />
    /// <autogeneratedoc />
    /// TODO Edit XML Comment Template for XunitSinkTarget
    [Target ( "Xunit" ) ]
    public class XunitSinkTarget : TargetWithLayout
    {
        public IMessageSink Sink { get ; set ; }

        public XunitSinkTarget ( string name )
        {
            OptimizeBufferReuse = false ;
            if ( name != null )
            {
                Name = name ;
            }
        }

        /// <summary>
        ///     Writes logging event to the log target. Must be overridden in inheriting
        ///     classes.
        /// </summary>
        /// <param name="logEvent">Logging event to be written out.</param>
        protected override void Write ( LogEventInfo logEvent )
        {
            var logMessage = RenderLogEvent ( Layout , logEvent ) ;

            Sink.OnMessage ( new Xunit.Sdk.DiagnosticMessage ( logMessage ) ) ;
        }

    }
}